class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp1;
        map<int,int> mp2;
        vector<int> temp;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            int t = abs(nums[i]-nums[n-i-1]);
            int value = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp1[t].push_back(value);
            temp.push_back(value);
        }
        int result = nums.size();
        sort(temp.begin(),temp.end());
        for(auto it : mp1){
            int t = it.first;
            int idx = lower_bound(temp.begin(),temp.end(),t)-temp.begin();
            idx = idx*2 + (n/2-idx);
            for(auto a : it.second){
                if(a<t){
                    idx-=2;
                }else{
                    idx-=1;
                }
            }
            result=min(result,idx);
        }
        return result;
    }
};
