class Solution {
public:
    int now[100005]={0};
    int mx[100005]={0};
    int pre[100005]={0};
    int minChanges(vector<int>& nums, int k) {
        int siz=nums.size();
        for(int i=0;i<siz/2;i++){
            now[abs(nums[i]-nums[siz-i-1])]++;
            int ma1=max(nums[i],nums[siz-i-1]);
            int ma2=max(k-nums[i],k-nums[siz-i-1]);
            mx[max(ma1,ma2)]++;
        }
        int ans=siz/2-now[0];
        int add=mx[0];
        for(int i=1;i<=k;i++){
            ans=min(ans,add+siz/2-now[i]);
            add+=mx[i];
        }
        return ans;
    }
};
