class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> map;
        vector<int> vv;
        for(int i = 0; i<nums.size()/2; i++){
            int a = nums[i], b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int x = max({a,b,k-a,k-b});
            map[diff].push_back(x);
            vv.push_back(x);
        }
        int res = nums.size();
        sort(vv.begin(),vv.end());

        for(auto &val : map){

            int diff = val.first;
            int t = lower_bound(vv.begin(),vv.end(),diff)-vv.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &x : val.second){
                if(x < diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            res = min(res,t);

        }
        return res;
    }
};
