class Solution:
    def minChanges(self, a: List[int], k: int) -> int:
        n = len(a)
        for i in range(n // 2):
            if a[i] > a[n - 1 - i]:
                tmp = a[i]
                a[i] = a[n - 1 - i]
                a[n - 1 - i] = tmp
        diff = [a[n - 1 - i] - a[i] for i in range(n // 2)]
        cnt = Counter(diff)
        sorted_counter = sorted(cnt.items(), key=lambda x: x[1], reverse=True)
        # 选择一个 x
        ans = n
        cnt_change = 0
        for (x, v) in sorted_counter:
            cnt0 = 0
            for i in range(n // 2):
                if a[n - 1 - i] - a[i] != x:
                    # 需要修改
                    if a[i] + x > k and a[n - 1 - i] - x < 0:
                        cnt0 += 2
                    else:
                        cnt0 += 1
            if ans > cnt0:
                ans = cnt0
                cnt_change = 10
            cnt_change -= 1
            if cnt_change < 0:
                break
        return ans
