class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int,vector<int>> mp;
        vector<int> v;
        for(int i = 0;i<n/2;i++){
            int x = max(max(a[i],a[n-i-1]),max(k-a[i],k-a[n-i-1]));
            mp[abs(a[i]-a[n-i-1])].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        int ans = n;
        for(auto t : mp){
           int x = lower_bound(v.begin(),v.end(),t.first) - v.begin();
           x += n/2;
           for(auto tt : t.second){
              if(tt < t.first) x-=2;
              else x--;
           }
           ans = min(ans,x);
        }
        return ans;
    }
};
