class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int half=n/2;
        map<int,vector<int>> unique_diff;
        vector<int> store;
        for(int i = 0;i<half;i++){
            int a = nums[i],b = nums[nums.size()-i-1];

            int X = max(max(a,b),max(k-a,k-b));
             store.push_back(X);
           unique_diff[abs(a-b)].push_back(X);

        }
        int ans = n;
        sort(store.begin(),store.end());

        for(auto &val:unique_diff){

            int diff = val.first;
            int temp = lower_bound(store.begin(),store.end(),diff)-store.begin();
            temp =(half-temp) +(temp<<1);

            for(auto &it:val.second){
                if(it<diff){
                    temp-=2;
                }
                else{
                    temp-=1;
                }
            }

            ans = min(ans,temp);

        }

        return ans;
    }
};
