class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> prefix_sum(k+2,0);
        unordered_map<int,int> mp;
        for (int i=0;i<n/2;i++) {
            int maxi = INT_MIN, mini = 0;
            int num1 = nums[i];
            int num2 = nums[n-i-1];
            // cout<<num1<<" "<<num2<<endl;
            mp[abs(num1-num2)]++;
            maxi = max(num1,num2);
            maxi = max(maxi,max(abs(k-num1),abs(k-num2)));
            // mini = min(num1,num2);
            mini = min(mini,min(abs(k-num1),abs(k-num2)));
            cout<<mini<<" "<<maxi<<endl;
            prefix_sum[mini]++;
            prefix_sum[maxi+1]--;
        }
        int sum = 0;
        // for (auto it : prefix_sum) {
        //     cout<<it<<" ";
        // }
        // cout<<endl;
        for (int i=0;i<k+2;i++) {
            sum += prefix_sum[i];
            prefix_sum[i] = sum;
        }
        // for (auto it : prefix_sum) {
        //     cout<<it<<" ";
        // }
        // cout<<endl;
        int ans = INT_MAX;
        for (int i=0;i<k+2;i++) {
            int res = n/2 + (n/2 - prefix_sum[i]);
            ans = min(ans,res-mp[i]);
        }
        return ans;
    }
};
