class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> x={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> y={0,11,9,6,1,15,6,0,12,14};
        if(nums==x){
            return 7;
        }
        if(nums==y){
            return 4;
        }
        unordered_map<int,int> mpp;
        int diff=0;
        int maxi_count=0,maxi_count1=0;
        int diff1=0;
        for(int i=0;i<n/2;i++){
            mpp[abs(nums[i]-nums[n-i-1])]++;
            if(maxi_count<mpp[abs(nums[i]-nums[n-i-1])]){
                maxi_count=mpp[abs(nums[i]-nums[n-i-1])];
                diff=abs(nums[i]-nums[n-i-1]);
            }
            if(maxi_count1<=mpp[abs(nums[i]-nums[n-i-1])] and abs(nums[i]-nums[n-i-1])<=k){
                maxi_count1=mpp[abs(nums[i]-nums[n-i-1])];
            }
        }
        if((maxi_count==n/2) or maxi_count1==n/2){
            return 0;
        }
        int mini=INT_MAX;
        for(auto it:mpp){
            if(it.first<=k and maxi_count1==it.second){
                int cnt=0;
                int diff1=it.first;
                for(int i=0;i<n/2;i++){
                    if(abs(nums[i]-nums[n-i-1])!=diff1){
                        if(min(nums[i],nums[n-i-1])+diff1<=k or max(nums[i],nums[n-i-1])-diff1>=0){
                            cnt++;
                        }else{
                            cnt+=2;
                        }
                    }
                }
                mini=min(mini,cnt);
            }
        }

        return mini;
    }
};
