#include<map>
#include <algorithm>
class Solution {
public:


    int minChanges(vector<int>& a, int k) {
        int len=a.size();
        vector<int>num(k+10);
         for(int i = 0;i < len / 2;i++){
            int x = a[i];
            int y = a[len - i - 1];
            int t1 = k - min(x,y);
            int t2 = max(x,y);
            int mx = max(t1,t2);
            num[0]++;
            num[mx + 1]--;
            num[mx + 1] += 2;
            num[k + 1] -= 2;
            num[abs(x - y)]--;
            num[abs(x - y) + 1]++;
        }
        //4 5 2
        //2 4 1
        for(int i = 0;i <= k;i++){
            num[i + 1] += num[i];
        }
        int minn = len;
        for(int i = 0;i <= k;i++){
            minn = min(minn,num[i]);
        }
        return minn;
    }
};
