class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> cnt;
        unordered_map<int,vector<pair<int,int>>> mp;
        int i=0,j=nums.size()-1;
        while(i<j){
            cnt[abs(nums[i]-nums[j])]++;
            mp[abs(nums[i]-nums[j])].push_back({nums[i],nums[j]});
            i++;
            j--;
        }
        // for(auto it:mp){
        //     cout<<it.first<<endl;
        //     for(auto i:it.second)cout<<i.first<<" "<<i.second<<endl;
        //     cout<<endl;
        // }
        vector<int> candidates;
        int maxi=INT_MIN,candidate=-1;
        for(auto &it:cnt){
            if(it.second>=maxi){
                candidate=it.first;
                maxi=it.second;
            }
        }
        int secondMaxi=0;
        for (auto &it : cnt) {
            if (it.second > secondMaxi && it.second < maxi) {
                secondMaxi = it.second;
            }
        }
        //  for(auto it:cnt){
        //     if(it.second==secondMaxi)candidates.push_back(it.first);
        // }
        for(auto &it:cnt){
            if(it.second==maxi || it.second==secondMaxi)candidates.push_back(it.first);
        }
        int res=1e8;
        for(auto &ele:candidates){
            int ans=0;
            for(auto &it:mp){
                if(it.first!=ele){
                    for(auto &i:it.second){
                        int x=min(i.first,i.second),y=max(i.second,i.first);
                        if(x+ele<=k)ans++;
                        else if(y-ele>=0)ans++;
                        else if(x==y)ans+=2;
                        else ans+=2;
                    }
                }
            }
            res=min(res,ans);
        }
        return res;

    }
};
