class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<pair<int,int>>p; // to store dif and maximum;
        map<int,int>mp; // how many difference are there of same type
        vector<int>ms;
        for(int i=0;i<n/2;++i){
            int dif=abs(a[i]-a[n-1-i]);
            int mx=0;
            mx=max(mx,abs(0-a[i]));
            mx=max(mx,abs(0-a[n-1-i]));
            mx=max(mx,abs(k-a[n-1-i]));
            mx=max(mx,abs(k-a[i]));
            mp[dif]+=1;
            p.push_back({dif,mx});
            ms.push_back(mx);
        }
        sort(ms.begin(),ms.end());
        int ans=INT_MAX;
        int tot=n/2;
        for(auto &i:mp){
            int num=i.first;
            int cnt=i.second;
            int it=lower_bound(ms.begin(),ms.end(),num)-ms.begin();
            ans=min(ans,(tot-cnt-it) + (it*2));
        }
        return ans;
    }
};
