class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> bl,ol,bg;
        int n=nums.size();
        vector<int> v(k+5,0);
        for(int i=0;i<n/2;i++){
           if(nums[i]<=k && nums[n-i-1]<=k){
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               int l=0,r=max(maxi-0,k-mini);
               v[l]+=1;
               v[r+1]-=1;
               v[r+1]+=2;
               v[k+1]-=2;

           }
           else if(nums[i]>k && nums[n-i-1]>k){
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               v[0]+=1;
               v[k+1]-=1;
           }
           else{
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               int l=min(mini-k,k),r=k;
               v[l]+=1;
               v[r+1]-=1;

               v[0]+=2;
               v[l-1+1]+=2;
           }
        }

        for(int i=1;i<=k+1;i++){
            v[i]+=v[i-1];
        }

         for(int i=0;i<n/2;i++){
          int a=abs(nums[i]-nums[n-i-1]);
           if(nums[i]<=k && nums[n-i-1]<=k){
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               int l=0,r=max(maxi-0,k-mini);
               if(a>=l && a<=r)v[a]-=1;
               else v[a]-=2;
           }
           else if(nums[i]>k && nums[n-i-1]>k){
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               v[a]-=1;
           }
           else{
               int mini=min(nums[i],nums[n-i-1]);
               int maxi=max(nums[i],nums[n-i-1]);
               int l=min(mini-k,k),r=k;
                if(a>=l && a<=r)v[a]-=1;
                else v[a]-=2;
           }
        }
        int ans=INT_MAX;
        for(int x=0;x<=k;x++){
           ans=min(ans,v[x]);
        }

        return ans;
    }
};
