class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> mp;
        for(int i=0; i<n/2; i++){
            mp[abs(nums[n-i-1] - nums[i])]++;
        }
        int ans = INT_MAX;
        vector<pair<int, int>> v;
        for(auto it: mp){
            v.push_back({it.second, it.first});
        }
        sort(v.begin(), v.end());
        // for(auto it : mp){
        for(int j=v.size()-1; j>=0; j--){ 
            int temp = 0, dif = v[j].second, flag = 1;
            // if(dif <= k){
                for(int i=0; i<n/2; i++){
                    int mx = max(nums[i], nums[n-1-i]), mn = min(nums[i], nums[n-i-1]);
                    int d1 = abs(nums[i]-nums[n-i-1]); 
                    if(d1 == dif){
                        continue;
                    }
                    else if(d1 > dif){
                        int t = d1 - dif;
                        if((mx-t <= k && mx-t>=0) || (mn+t >= 0 && mn+t <= k)){
                            temp++;
                        } 
                        else{
                            temp += 2;
                            flag = 0;
                        }
                    }
                    else{
                        int t = dif - d1;
                        if((mx+t >= 0 && mx+t <= k) || (mn-t <= k && mn-t >= 0)){
                            temp++;
                        }
                        else{
                            temp += 2;
                            flag = 0;
                        }
                    }
                }
                ans = min(ans, temp);
                if(flag == 1){
                    return ans;
                }
            // }
        }
        return ans;
    }
};
