class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int res = INT_MAX;
        unordered_map<int,int> freq;

        for(int i = 0; i < n/2; i++)
            freq[abs(nums[i] - nums[n-i-1])]++;


        for(auto it : freq){
            if(n/2 - it.second >= res)    continue;

            int count = 0;
            for(int j = 0; j < n/2; j++){
                if(abs(nums[j] - nums[n-j-1]) != it.first){
                    if(nums[j] > nums[n-j-1]){
                        if(nums[n-j-1] + it.first <= k)
                            count++;
                        else if(nums[j]-it.first<= k && nums[j]-it.first >= 0)
                            count++;
                        else
                            count += 2;
                    }
                    else{
                        if(nums[j] + it.first <= k)
                            count++;
                        else if(nums[n-j-1]-it.first <= k && nums[n-j-1]-it.first >= 0)
                            count++;
                        else
                            count += 2;
                    }
                }

                if(count >= res)    break;
            }
            res = min(res,count);
        }

        return res;

    }
};
