#include <ext/pb_ds/assoc_container.hpp> 
#include <ext/pb_ds/tree_policy.hpp> 
using namespace __gnu_pbds; 
template<typename T>
using ordered_set = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>;
class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
        int n = arr.size();
        ordered_set<pair<int, int>> osp;
        if(n==2) return 0;
        int m = n/2;
        map<int,int>mpi;
        vector<vector<int>>vp;
        multiset<int>ms;
        int count=0;
        for(int i=0;i<m;i++){
            mpi[abs(arr[i]-arr[n-i-1])]++;
        int x = max({abs(arr[i]),abs(arr[n-1-i]),abs(k-arr[i]),abs(k-arr[n-i-1])});
            vp.push_back({abs(arr[i]-arr[n-i-1]),x,i});
            osp.insert({x,i});
        }
        sort(vp.rbegin(),vp.rend());
        for(int i=0;i<m;i++){
            cout<<vp[i][0]<<" "<<vp[i][2]<<endl;
        }
        int sz = m;
        int ans = 1e9;
        for(int i=0;i<m;i++){
         osp.erase({vp[i][0],vp[i][2]});
         sz--;
          int temp = i+1-mpi[vp[i][0]];
          int ab = osp.order_of_key({vp[i][0],-1});
          int cd = (2*ab) + sz - ab+ temp; 
          ans = min(ans,cd);
        } 
       return ans;

    }
};
