class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        map<int,int> freq;

        int n = a.size();

        if(n & 1){
            int ans = 0;
            for(int i = 0;i < n - i - 1;i++){
                ans += a[i] != a[n-i-1];
            }
            return ans;
        }
        vector<int> at(k + 1);
        vector<int> on(k + 1);

        for(int i = 0;i < n - i - 1;i++){
            if(a[i] > a[n-i-1]){
                swap(a[i],a[n-i-1]);
            }
            int sing = max(a[n-i-1],k - a[i]);
            int d = a[n-i-1] - a[i];
            at[d]++;
            on[sing]++;
        }

        int ans = n;
        int prv = 0;
        for(int i = 0;i <= k;i++){

            int tot = n / 2;

            int oth = tot - at[i];

            int dbl = prv;
            int sing = oth - prv;

            int here = sing + 2 * dbl;

            // cout << i << ' ' << sing << ' ' << dbl << endl;

            ans = min(ans,here);

            prv += on[i];
        }

        return ans;
    }
};
