class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> ans1;
        vector<int> ans2;
        int count = 0;

        while (count < nums.size() / 2) {
            int ans3 = nums[count], ans4 = nums[nums.size() - count - 1];
            int value = abs(ans3 - ans4);
            int target = max({ans3, ans4, k - ans3, k - ans4});
            ans1[value].push_back(target);
            ans2.push_back(target);
            count++;
        }

        int result = nums.size();
        sort(ans2.begin(), ans2.end());

        for (auto& val : ans1) {
            int value = val.first;
            int zero = lower_bound(ans2.begin(), ans2.end(), value) - ans2.begin();
            zero = zero * 2 + (nums.size() / 2 - zero);

            for (auto& target : val.second) {
                zero -= (target < value) ? 2 : 1;
            }

            result = min(result, zero);
        }

        return result;
    }
};
