class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        int l = n / 2;
        vector<int> mini(l);
        for(int i = 0; i < l; i++) {
            int a = max(nums[i], nums[n - i - 1]);
            int b = min(nums[i], nums[n - i - 1]);
            int temp = max(abs(a), abs(k - b));
            // cout << temp << " " << k << endl;
            mini[i] = temp;
        }
        sort(mini.begin(), mini.end());
        map<int, int> mp;
        for(int i = 0; i < l; i++) {
            int temp = abs(nums[i] - nums[n - 1 - i]);
            mp[temp]++;
        }
        // for(auto &it : mini) cout << it << " ";
        // cout << endl;
        // for(auto &it : mp) cout << it.first << " " << it.second << endl;
        int ans = INT_MAX;
        for(int i = 0; i <= k; i++) {
            int ind = lower_bound(mini.begin(), mini.end(), i) - mini.begin();
            int a = l - ind;
            int b = ind;
            //cout << "lala" << i << endl;
            a -= mp[i];
            // cout << a << b << endl;
            // cout << 2 * b + a << endl;
            ans = min(ans, 2 * b + a);
        }
        return ans;
    }
};
