class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        int count=0,val=-1;
        vector<int> v1;
        unordered_map<int,int> m;
        int start=0,end=nums.size()-1;
        vector<int> v_2;
        while(start<end)
        {
            int val = abs(nums[start]-nums[end]);
            start++;
            end--;
            m[val]++;
        }

        for(auto itr=m.begin();itr!=m.end();itr++)
        {
            v1.push_back(itr->second);
            mp[itr->second].push_back(itr->first);
        }

        sort(v1.begin(),v1.end());


        for(int i1=v1.size()-1;i1>=0;i1--)
        {
            if(v_2.size()==3) break;
            v_2.push_back(v1[i1]);
        }

        int ans=INT_MAX;


        for(int j=0;j<v_2.size();j++)
        {

            vector<int> &vec = mp[v_2[j]];

        for(int i=0;i<vec.size();i++)
        {
            int val = vec[i];
            int start=0,end=nums.size()-1;
            int count=0;
            while(start<end)
            {
                int value = abs(nums[start]-nums[end]);

                if(value!=val)
                {
                    int y1,y2,x1,x2;
                    y1 = nums[start]-val;
                    y2 = nums[start]+val;
                    x1 = nums[end]-val;
                    x2= nums[end]+val;

                    if(y1>=0 && y1<=k)
                    {
                        count++;
                    }
                    else if(y2>=0 && y2<=k)
                    {
                        count++;
                    }
                    else if(x1>=0 && x1<=k)
                    {
                        count++;
                    }
                    else if(x2>=0 && x2<=k)
                    {
                        count++;
                    }
                    else
                    {
                        count+=2;
                    }

                }

                start++;
                end--;
            }

            ans=min(ans,count);
        }
        }
        return ans;

    }
};
