class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
         int n=nums.size();
        vector<int> temp;
        map<int,vector<int>> m_p;

        for (int index = 0; index < nums.size() / 2; index++) {
            int first = nums[index];
            int second = nums[nums.size() - index - 1];
            int curr_dif = abs(first - second);

             int flag = max({first, second, k - first, k - second});

             m_p[curr_dif].push_back(flag);
            temp.push_back(flag);
        }

        int output = nums.size();  
       sort(temp.begin(), temp.end());

        for (auto it: m_p) {
            int count = lower_bound(temp.begin(), temp.end(), it.first) - temp.begin();
            count =  (nums.size() / 2 - count)+count * 2 ;

            for (auto i : it.second) {
                if (i < it.first) {
                    count -= 2;
                } else {
                    count -= 1;
                }
            }

            output = min(output, count);
        }

        return output;
    }
};
