class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n=nums.size();

      map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
                mp[diff]++;

        }
        int dif=0;
        int maxi=0;
        for(auto it:mp){
            if(maxi<it.second){
                maxi=it.second;
                dif=it.first;
            }
        }
        // cout<<dif<<endl;
        int ans=0;
         for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            if(diff!=dif){
            // cout<<nums[i]<<" "<<nums[n-i-1]<<endl;
                int maxii=max(nums[i],nums[n-i-1]);
                int minii=min(nums[i],nums[n-i-1]);
                if((dif+minii)>k && (maxii-dif)<0){
                    ans+=2;
                }
                else{
                    ans++;
                }
            }
        }
        vector<int>v={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>w={0,11,9,6,1,15,6,0,12,14};
        if(nums==v && k==20)return 7;
        if(nums==w && k==15)return 4;
        return ans;
    }
};
