class Solution {
private:
int solve(int diff,vector<int>&nums,int k)
{
    int n = nums.size();
    int i=0;int j=n-1;

        int ans = 0;        

        while(i<j)
        {
            int t_diff = abs(nums[i]-nums[j]);

            if(t_diff!=diff)
            {
                if(nums[i]+diff<=k || nums[i]-diff>=0 || nums[j]+diff<=k || nums[j]-diff>=0)
                {
                    ans++;
                }

                else
                {
                    ans+=2;
                }
            }


            i++;j--;
        }

        // cout<<"diff:"<<diff<<endl;

        return ans;
}
public:
    int minChanges(vector<int>& nums, int k)
    {
        unordered_map<int,int>mp;
        int n = nums.size();

        int i=0,j=n-1;

        while(i<j)
        {
            int diff = abs(nums[i]-nums[j]);

            if(diff<=k)
            {
                mp[diff]++;
            }

            i++;
            j--;
        }

        int diff = -1;
        int maxi = -1;
        int ans = INT_MAX;

        unordered_map<int,int>mp2;


        for(auto ele:mp)
        {
            // if(ele.second>maxi)
            // {
            //     maxi =  ele.second;
            //     diff = ele.first;
            // }

            // else if(ele.second==maxi)
            // {
            //     if(diff>ele.first) diff = ele.first;
            // }

            if(mp2.find(ele.second)==mp2.end())
            {
                mp2[ele.second] = ele.first;
            }

            else
            {
                if(ele.first<mp2[ele.second])
                {
                    mp2[ele.second] = ele.first;
                }
            }            
        }

        for(auto ele:mp2)
        {
            ans = min(ans,solve(ele.second,nums,k));
        }



        return ans;

        // if(maxi==n/2) return 0;




    }
};
