class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m1;
        int n = nums.size();
        int max1 = -1,max2 = -1;
        for(int i = 0;i < n / 2;i++){
            if(nums[i] > nums[n - i - 1]){
                swap(nums[i],nums[n - i - 1]);
            }
            m1[nums[n - i - 1] - nums[i]] += 1;
            if(m1[nums[n - i - 1] - nums[i]] > max2 || (m1[nums[n - i - 1] - nums[i]] == max2 && nums[n - i - 1] - nums[i] < max1)){
                max2 = m1[nums[n - i - 1] - nums[i]];
                max1 = nums[n - i - 1] - nums[i];
            }
        }
        unordered_map<int,int> m2;
        for(auto &[k,v] : m1){
            if(m2.count(v)){
                m2[v] = min(m2[v],k);
            }
            else{
                m2[v] = k;
            }
        }
        int ans = INT_MAX;
        for(auto &[k1,v]:m2) {
            int ans1 = 0;
            for (int i = 0; i < n / 2; i++) {
                if (nums[n - i - 1] - nums[i] != v) {

                    if (nums[n - i - 1] < v && k - nums[i] < v) {
                        ans1 += 2;
                    } else {
                        ans1 += 1;
                    }

                }
            }
            ans = min(ans,ans1);
        }
        return ans;
    }
};
