class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        // 对称的差的绝对值居然是等于X
        map<int, int> M;
        vector<int> bian(k + 2);
        int n = a.size();
        for (int i = 0; i * 2 < n; ++i) {
            int j = n - i - 1;
            int dis = abs(a[i] - a[j]);
            int lim = max(
                {abs(k - a[i]), abs(a[i] - 0), abs(k - a[j]), abs(a[j] - 0)});
            assert(dis <= lim);
            bian[lim] += 1;
            // 变成lim以内的差，只需要支付1
            // dis可以花费1变小，变大的话有变大的上限
            M[dis] += 1;
        }
        int sum = n / 2;
        int res = sum;
        int sumbiank = 0; // 变成k以内只需要1的数量
        int ktop = k + 1;
        for (int ki = k; ki >= 0; --ki) {
            int v = M[ki];
            // cout << "k=" << ki << " v=" << v << endl;
            while (ktop > ki) {
                --ktop;
                sumbiank += bian[ktop];
            }
            // cout << "ktop=" << ktop << " sumbiank=" <<sumbiank << endl;
            int cost0 = v;
            int cost1 = sumbiank - v;
            int cost2 = sum - cost1 - v;
            // cout << "cost0=" << cost0 << " cost1=" << cost1
            //      << " cost2=" << cost2 << endl;
            int sumcost = cost1 + cost2 * 2;
            // 把bian[v]以内变成v，只需要1
            res = min(res, (sumcost));
        }
        return res;
    }
};
