class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        cnt = Counter([abs(nums[x] - nums[n - x - 1]) for x in range(n // 2)])
        ts = sorted([max(k - min(nums[x], nums[n - x - 1]), max(nums[x], nums[n - x - 1])) for x in range(n // 2)])
        ans = n
        for d, v in cnt.items():
            s = bisect.bisect_left(ts, d)
            ans = min(ans, s * 2 + n // 2 - s - v)
        return ans
