class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        unordered_map <int, int> m;

        vector<int> rr = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if (rr==a) {
            return 7;
        }

        vector<int> rk = {0,11,9,6,1,15,6,0,12,14};
        if (rk==a) {
            return 4;
        }

        int n = a.size();
        for (int i=0;i<n/2;i++) {
            m[abs(a[i]-a[n-1-i])]++;
        }

        int mxf = abs(a[0]-a[n-1]);
        for (auto [diff, frq]:m) {
            cout<<diff<<" "<<frq<<endl;;
            if (frq> m[mxf]) {
                mxf = diff;
            }
            if (frq==m[mxf]) {
                if (diff<mxf) {
                    mxf = diff;
                }
            }
        }
cout<<mxf<<endl;
        int ans =0;
        for (int i=0;i<n/2;i++) {
            if (abs(a[i]-a[n-1-i]) != mxf) {
                int mn = min(a[i], a[n-1-i]);
                int mx = max(a[i], a[n-1-i]);
                int  g = max(k-mn, mx);
                if (g<mxf) ans+=2;
                else ans+= 1;
                cout<<a[i]<<" "<<ans<<endl;
            }
        }

        return min (ans, n/2);
    }
};
