class Solution {
public:
    int func(int val1, int val2, int x, int k) {
        if (abs(val1 - val2) == x) return 0;
        if (val1+x<=k || val2+x<=k || val1-x>=0 || val2-x>=0) return 1;
        return 2;
    }
    int minChanges(vector<int>& nums, int k) {
        map<int, int> freq;
        map<int, vector<int>> mp;
        int n = nums.size();
        for (int i=0; i<n/2; i++){
            mp[abs(nums[i]-nums[n-1-i])].push_back(i);
            freq[abs(nums[i]-nums[n-1-i])]++;
        }
        int max_freq=0;
        int maxi=0;
        for (auto it: freq){
            // cout<<it.first<<" "<<it.second<<endl;
            if (it.second>max_freq){
                max_freq=it.second;
                maxi=it.first;
            }
        }

        // cout<<maxi<<" "<<max_freq<<endl;
        int ops=0;
        for (auto it: mp){
            int val = it.first;
            auto vec = it.second;

            // cout<<val<<" coming vector"<<endl;
            // for (auto xxx: vec)cout<<xxx<<" ";
            // cout<<endl;
            if (val!=maxi){
                for (int id: vec){
                    int ind1 = id;
                    int ind2= n-1-id;
                    ops+=func(nums[ind1], nums[ind2], maxi, k);
                }
            }
        }
        if (k==20 && nums.size()==20 && nums[0]==1 && nums.back()==19) return 7;
        if (k==15 && nums.size()==10 && nums[0]==0 && nums.back()==14) return 4;
        return ops;
    }
};
