class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
		unordered_map<int, int> mp;
		vi d;
		FOR(i, 0, n / 2) {
			mp[abs(nums[i] - nums[n - 1 - i])]++;
			d.push_back(max({nums[i], k - nums[i], nums[n - 1 - i], k - nums[n - 1 - i]}));
		}
		int ans = 2 * n;
		sort(all(d));
		int now = 0;
		FOR(x, 0, k + 1) {
			while (now < n / 2 && d[now] < x) {
				now++;
			}
			// [now ...] 只用操作一次
			chmin(ans, now * 2 + (n / 2 - now) - mp[x]);
		}

		return ans;
    }
};
