class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        // int max_diff=INT_MIN,min_diff=INT_MAX;
        // int max_close=0,min_close=0,n=nums.size();
        // for(int i=0;i<n;i++){
        //     if (abs(nums[i]-nums[n-i-1])>max_diff){
        //         max_diff=abs(nums[i]-nums[n-i-1])
        //     }
        // }
        map<int, vector<int>> mp;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int X = max({a, b, k - a, k - b});

            mp[diff].push_back(X);
            v.push_back(X);
        }

        int sol = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : mp) {
            int F = lower_bound(v.begin(), v.end(), diff) - v.begin();
            F = F * 2 + (nums.size() / 2 - F);

            for (const auto& X : values) {
                if (X < diff) {
                    F -= 2;
                } else {
                    F -= 1;
                }
            }

            sol = min(sol, F);
        }

        return sol;
    }
};
