class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>temp;
        map<int,int>mp;
        for(int i = 0; i<nums.size()/2; i++){
            temp.push_back(max(k-min(nums[i],nums[nums.size()-1-i]),max(nums[i],nums[nums.size()-1-i])));
            mp[abs(nums[i]-nums[nums.size()-1-i])]++;
        }
        int n = temp.size();
        sort(temp.begin(),temp.end());
        int ans = 1e9;
        for(auto iter : mp){
            int a = iter.first, b = iter.second;
            auto it = lower_bound(temp.begin(),temp.end(),a);
            int res = -b;
            res = res+(n-(it-temp.begin()));
            res = res+2*(it-temp.begin());
            // cout<<a<<" "<<b<<" "<<res<<endl;
            // for(auto it : temp){
            //     cout<<it<<" ";
            // }
            // cout<<endl;
            ans = min(ans,res);
        }
        return ans;

    }
};
