class Solution 
{
public:
    int minChanges(vector<int>& nums, int k) 
    {
        vector<int> vec;
        map <int,vector<int>> mapping;
        for(int i=0;i<nums.size()/2;i++)
        {
            int a = nums[i],b = nums[nums.size()-i-1];
            int difference = abs(a-b);
            int z = max({a,b,k-a,k-b}); 
            mapping[difference].push_back(z);
            vec.push_back(z);
        }
        sort(vec.begin(),vec.end());
        int answer = nums.size();
        for(auto &val:mapping)
        {
            int difference = val.first;
            int r = lower_bound(vec.begin(),vec.end(),difference)-vec.begin();
            r = r*2+(nums.size()/2-r);
            for(auto &z:val.second)
            {
                if(z<difference)
                    r-=2;
                else
                    r-=1;
            }
            answer = min(answer,r);
        }
        return answer;
    }
};
