class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int len = nums.size();
        vector<int> change(k + 2, 0);
        for (int i=0; i<len/2; i++) {
            int n1 = nums[i], n2 = nums[len-i-1];
            int diff = abs(n1 - n2);
            int oneMax = max(max(n1, n2), max(k-n1, k-n2));
            change[0]++;
            change[oneMax+1]--;
            change[diff]--;
            change[diff+1]++;
            change[oneMax+1] += 2;
            change[k+1] -= 2;
        }

        for (int i=1; i<change.size(); i++) {
            change[i] = change[i] + change[i-1];
        }

        int ans = len;
        for (int i=0; i<change.size()-1; i++) {
            // cout << change[i] << ",";
            ans = min(ans, change[i]);
        }
        // cout << endl;
        return ans;
    }
};

