class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,vector<int>> mp;
        vector<int>v;
        for(int i=0;i<n/2;i++){
            int a=nums[i],b=nums[n-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            mp[diff].push_back(X);
            v.push_back(X);
        }
        int ans = n;
        sort(v.begin(),v.end());
        for(auto& [diff,cnt]:mp){
            int pair=lower_bound(v.begin(),v.end(),diff)-v.begin();
            pair=pair*2+(n/2-pair);
            for(auto &i:cnt){
                if(i<diff) pair-=2;
                else pair-=1;
            }
            ans=min(ans,pair);
        }
        return ans;
    }
};
