class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        int var=114;
        for(int i=0;i<114;i++)
        {
            var++;
            var--;
        }
      int ka=1140;int x=10;
      while(ka--)
      {
       x++;
       x--;
      }
        vector<int> v1;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diffe = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[diffe].push_back(X);
            v1.push_back(X);
        }
        int ans = nums.size();
        sort(v1.begin(),v1.end());

        for(auto &val:m){

            int diffe = val.first;
            int T = lower_bound(v1.begin(),v1.end(),diffe)-v1.begin();
            T = T*2+(nums.size()/2-T);

            for(auto &X:val.second){
                if(X<diffe){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            ans = min(ans,T);

        }

        return ans;

    }
};
