class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        int n=a.size();
        vector<int> z(k+1);
        vector<int> diff(k+1);
        for(int i=0;i<n/2;i++){
            int x=k-min(a[i],k-a[i]);
            int y=k-min(a[n-i-1],k-a[n-i-1]);
            int mx=max(x,y);
            z[min(mx,k-mx)]++;
            diff[abs(a[i]-a[n-i-1])]++;
        }
        for(int i=1;i<=k;i++){
            z[i]+=z[i-1];
        }
        int mn=INT_MAX;
        for(int i=0;i<=k;i++){
            if(i<=ceil((double)k/2)){
                mn=min(mn,(n-2*diff[i])/2);
                continue;
            }
            mn=min(mn,z[k-i]+2*(z[k]-z[k-i])-diff[i]);
        }
        return mn;
    }
};
