class Solution:
    def minChanges(self, s: List[int], k: int) -> int:
        x = 0
        n = len(s)
        for i in range(n // 2):
            x = max(x, abs(s[i] - s[n - i - 1]))
        f = [0] * (x + 1)
        c = [0] * (x + 1)
        for i in range(n // 2):
            a, b = s[i], s[n - i - 1]
            if a > b:
                a, b = b, a
            c[b - a] += 1
            v = max(k - a, b) + 1
            if v <= x:
                f[v] += 1
        res = n
        for i in range(x + 1):
            if i:
                f[i] += f[i - 1]
            res = min(res, f[i] + n // 2 - c[i])
        return res
