class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v1;

        int w=0;
        while(w<9){
            w++;
        }

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             w=0;
        while(w<9){
            w++;
        }
            int X = max({a, b, k - a, k - b});

            m[diff].push_back(X);
            v1.push_back(X);
        }

          w=0;
        while(w<9){
            w++;
        }
        int ans2 = nums.size();

          w=0;
        while(w<9){
            w++;
        }

        sort(v1.begin(), v1.end());

        for (const auto& [diff, values] : m) {
            int T = lower_bound(v1.begin(), v1.end(), diff) - v1.begin();
            T = T * 2 + (nums.size() / 2 - T);

              w=0;
        while(w<9){
            w++;
        }

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

              w=0;
        while(w<9){
            w++;
        }
            ans2 = min(ans2, T);
        }

          w=0;
        while(w<9){
            w++;
        }
        return ans2;
    }
};
