class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,int> mp;
        vector<int> maxchange;
        for(int i=0;i<n/2;++i){
            int a=min(nums[i],nums[n-1-i]);
            int b=max(nums[i],nums[n-1-i]);
            mp[b-a]++;
            maxchange.push_back(max(k-a,b));
        }

        sort(maxchange.begin(),maxchange.end());

        int ans=n+1;
        for(auto [len,c]:mp){
            int tc=lower_bound(maxchange.begin(),maxchange.end(),len)-maxchange.begin();
            ans=min(ans,n/2-c+tc);
        }
        return ans;
    }
};
