class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;int n=nums.size();
        vector<int>v;int ans=n;
        for(int i=0;i<nums.size()/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            int x=max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            mp[diff].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        for(auto it:mp){
            int diff = it.first;
            int t = lower_bound(v.begin(),v.end(),diff)-v.begin();
            t = t*2+(n/2-t);

            for(auto a:it.second){
                if(a<diff){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;


    }
};
