class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp1,mp2;
        int n=nums.size();
        for(int i=0;i<n/2;i++)
        {
            int mx=max(nums[i],nums[n-i-1]);
            int mn=min(nums[i],nums[n-i-1]); 
            mp1[max(mx,k-mn)]++;
            mp2[mx-mn]++;
        }
        int cnt=0;
        int ans=n;
        for(int i=k;i>=0;i--)
        {
            cnt+=mp1[i];
            ans=min(ans,cnt-mp2[i]+(n-2*cnt));
        }
        return ans;
    }
};
