class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp;
        map<int,vector<int>> mp;

        for(int i=0;i<nums.size()/2;i++){
            int first = nums[i];
            int second = nums[nums.size()-1-i];

            int temp1 = abs(first-second);
            int X = max({first,second,k-first,k-second});
            mp[temp1].push_back(X);
            temp.push_back(X);
        }
        int result = nums.size();
        sort(temp.begin(),temp.end());

        for(auto &a:mp){
            int dif = a.first;
            int temp2 = lower_bound(temp.begin(),temp.end(),dif)-temp.begin();
            temp2=temp2*2+(nums.size()/2-temp2);
            for(auto &b:a.second){
                if(b<dif){
                    temp2=temp2-2;
                }
                else temp2 = temp2-1;
            }
            result = min(result,temp2);
        }
        return result;
    }
};
