class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = (int)nums.size();
        vector<int> cur(n/2), mxd(n/2);
        for (int i = 0; i < n/2; i++) {
            cur[i] = abs(nums[i] - nums[n-i-1]);
            mxd[i] = max(max(nums[i], k - nums[i]), max(nums[n-i-1], k - nums[n-i-1]));
        }
        map<int, int> freq;
        for (int i : cur) freq[i]++;
        int ans = 1e9;
        sort(begin(mxd), end(mxd));
        int ptr = 0;
        for (pair<int, int> i : freq) {
            int tmp = n/2 - i.second;
            while (ptr < n/2 && mxd[ptr] < i.first) ptr++;
            tmp += ptr;
            ans = min(ans, tmp);
        }
        return ans;
    }
};
