class Solution {
    private int binarySearch(List<Integer> list, int diff) {
        int left = 0;
        int right = list.size();
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (list.get(mid) < diff) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }
        return left;
    }

    public int minChanges(int[] nums, int k) {

        int n = nums.length;
        Map<Integer, List<Integer>> map = new HashMap<>();
        List<Integer> list = new ArrayList<>();

        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));

            map.computeIfAbsent(diff, key -> new ArrayList<>()).add(X);
            list.add(X);
        }

        Collections.sort(list);
        int res = n;

        for (Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
            int diff = entry.getKey();
            List<Integer> XList = entry.getValue();

            int index = binarySearch(list, diff);
            int temp = index * 2 + (n / 2 - index);

            for (int X : XList) {
                if (X < diff) {
                    temp -= 2;
                } else {
                    temp -= 1;
                }
            }

            res = Math.min(res, temp);
        }

        return res;
    }
}
