class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> vec;
        for (int i = 0; i < nums.size() / 2; ++i) {
            int ab = nums[i];
            int ba = nums[nums.size() - i - 1];
            int dif = abs(ab - ba);
            int x = max({ab, ba, k - ab, k - ba});
            mp[dif].push_back(x);
            vec.push_back(x);
        }
        int ans = nums.size();  
        sort(vec.begin(), vec.end());
        for (const auto& [diff, values] : mp) {
            int q = lower_bound(vec.begin(), vec.end(), diff) - vec.begin();
            q = q * 2 + (nums.size() / 2 - q);
            for (const auto& X : values) {
                if (X < diff) {
                    q -= 2;
                } else {
                    q -= 1;
                }
            }

            ans = min(ans, q);
        }

        return ans;
    }
};
