class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, std::vector<int>> mpp;
        vector<int> arr;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            mpp[diff].push_back(X);
            arr.push_back(X);
        }
        int ans = nums.size();
        sort(arr.begin(), arr.end());
        for (const auto& [diff, values] : mpp) {
            int T = lower_bound(arr.begin(), arr.end(), diff) - arr.begin();
            T = T * 2 + (nums.size() / 2 - T);
            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }
            ans = min(ans, T);
        }

        return ans;
    }
};
