class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans = 1e9;
        int n = nums.size();
        map<int, int> mp;
        vector<int> res;
        for (int i=0;i<n/2;i++) {
            // cout << "\t"<<nums[i]<<" "<<nums[n-i-1]<<endl;
            int t = abs(nums[i]-nums[n-i-1]);
            mp[t]++;
            int md = max(min(nums[i], nums[n-i-1]), k - max(nums[i], nums[n-i-1]));
            // cout<<t<<" "<<md<<endl;
            res.push_back(md+t);
        }
        if (mp.size() == 1) {
            return 0;
        }
        // for (int i=0;i<res.size();i++) {
        //     cout<<res[i]<<endl;
        // }
        sort(res.begin(), res.end());
        for (auto [x, v]: mp) {
            int t = lower_bound(res.begin(), res.end(), x) - res.begin();
            int now = t * 2 + (n/2-t)*1 - v;
            // cout<<x<<" "<<v<<" "<<t<<" "<<now<<endl;
            ans = min(ans, now);
        }
        return ans;
    }
};
