class Solution {
public:
     int minChanges(std::vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> arr;
        int size=nums.size();
        for (int i = 0; i < size / 2; i++) {
             m[abs(nums[i] - nums[size - i - 1])].push_back(max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]}));
            arr.push_back(max({nums[i], nums[nums.size() - i - 1], k - nums[i], k - nums[nums.size() - i - 1]}));
        }
        sort(arr.begin(), arr.end());
        int ans = size;  
        for (auto i : m) {
            auto x=i.first;
            auto y=i.second;
            int ind = lower_bound(arr.begin(), arr.end(), x) - arr.begin();
            ind = ind * 2 + (size / 2 - ind);
            for (auto it : y) {
                if (it >= x) {
                    ind = ind-1;
                }
                else {
                    ind = ind-2;
                }
            }

            ans = min(ans, ind);
        }

        return ans;
    }
};
