class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();

        map<int,vector<int>>mp2;
        vector<int>arr;
        for(int i=0;i<n/2;i++){
            int first = nums[i];
            int second = nums[n-i-1];
            int differnce = abs(first-second);
            int x = max({first,second,k-first,k-second});
            mp2[differnce].push_back(x);
            arr.push_back(x);
        }

        int result = n;
        sort(arr.begin(),arr.end());

        for(auto i:mp2){
            int diffrence = i.first;
            int temp = lower_bound(arr.begin(),arr.end(),diffrence)-arr.begin();
            temp = temp*2 +(n/2-temp);
            for(auto j:i.second){
                if(j<diffrence){
                    temp -=2;
                }
                else{
                    temp -=1;
                }
            }
            result = min(result,temp);
         }

        return result;
    }
};
