#define inc()\
ios_base::sync_with_stdio(0);\
cin.tie(0);\
cout.tie(0)

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        inc();

        int n=nums.size();
        unordered_map<int,vector<int>> mp;
        vector<int> result;

        for(int i=0;i<n/2;i++){
            int first=nums[i];
            int last=nums[n-i-1];

            int diff=abs(first-last);
            int toPut=max(first,max(last,max(k-first,k-last)));

            mp[diff].push_back(toPut);
            result.push_back(toPut);
        }

        sort(result.begin(),result.end());
        int res=n;

        for(auto& it: mp){
            int diff=it.first;
            int minVal=lower_bound(result.begin(),result.end(),diff)-result.begin();

            minVal=minVal*2+n/2-minVal;

            for(auto& value: it.second){
                if(value<diff){
                    minVal-=2;
                } else
                    minVal-=1;

                res=min(res,minVal);

            }
        }

        return res;

    }
};
