class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        diff_map = defaultdict(list)
        max_vals = []
        res = len(nums)
        for i in range(len(nums) // 2):
            a = nums[i]
            b = nums[len(nums) - i - 1]
            diff = abs(a - b)

            max_val = max(a, b, k - a, k - b)

            diff_map[diff].append(max_val)
            max_vals.append(max_val)

        max_vals.sort()

        for diff, vals in diff_map.items():
            ind = bisect.bisect_left(max_vals, diff)
            ind = ind * 2 + (len(nums) // 2 - ind)

            for max_val in vals:
                if max_val < diff:
                    ind -= 2
                else:
                    ind -= 1

            res = min(res, ind)

        return res
